package com.fit.kmm.khttpimport com.fit.kmm.khttp.ui.IKLoadingimport com.fit.kmm.khttp.ui.IKToast/** * NetHelper base class. used for android and ios nethelper class. * @author delanding * */interface INetHelper {    fun withTimeout(to: Long): INetHelper    fun withLoading(l: IKLoading?): INetHelper    fun withErrorToast(b: IKToast?): INetHelper    /**     * set the http request header, only post request used.     * @param header the header     * */    fun withHeader(header: Map<String, String>): INetHelper    /**     * 添加cookie到header中     **/    fun withCookie(cookie: Map<String, Any>): INetHelper    /**     * http get request     * @param url the url which will be request.     * @param iNet callback of the request     * */    fun get(url: String, iNet: INet)    /**     * http post request     * @param url the url which will be request.     * @param iNet callback of the request     * */    fun jsonStrPost(url: String, jsonMap: Map<String, Any>, iNet: INet)    /**     * http post request     * @param url the url which will be request.     * @param iNet callback of the request     * */    fun jsonPost(url: String, params: Map<String, Any>, iNet: INet)    /**     * http post request with Content-Type: application/x-www-form-urlencoded     * @param url the url which will be request.     * @param iNet callback of the request     * */    fun formPost(url: String, params: Map<String, Any>, iNet: INet)    /**     * http get request for bytes     * @param url the url which will be request.     * @param callback callback of the request, contains bytes of the request.     * */    fun download(url: String, callback: INetDownload)    /**********simply func for IOS**************/    fun get(        url: String,        callback: (            retCode: Int, header: Map<String, List<String>>,            body: String        ) -> Unit    )    fun jsonStrPost(        url: String,        jsonMap: Map<String, Any>,        callback: (            retCode: Int, header: Map<String, List<String>>,            body: String        ) -> Unit    )    fun jsonPost(        url: String,        params: Map<String, Any>,        callback: (            retCode: Int, header: Map<String, List<String>>,            body: String        ) -> Unit    )}