package com.fit.kmm.klogerimport kotlin.jvm.JvmStaticimport kotlin.native.concurrent.ThreadLocal@ThreadLocalobject KLoger {    var logger: IKLoger? = null    @JvmStatic    fun v(msg: String) = logger?.v(msg)    @JvmStatic    fun i(msg: String) = logger?.i(msg)    @JvmStatic    fun d(msg: String) = logger?.d(msg)    @JvmStatic    fun e(msg: String) = logger?.e(msg)}